sys_ui_policy.script_true:
function onCondition() {

    if (g_form.isNewRecord()) {
        g_form.setReadOnly('u_pre_approved_software', false);
        g_form.setDisplay('u_pre_approved_software', true);
        g_form.setSectionDisplay('notes_andcomments', false);
		g_form.setMandatory("assigned_to", false);
        g_form.setSectionDisplay('stakeholders', false);
    } else {
        if (g_form.getValue('u_pre_approved_software') == '')
            g_form.setDisplay('u_pre_approved_software', false);
        if (!g_user.hasRole('x_gis_girm_assessm.software_assessor')) {
            g_form.setSectionDisplay('notes_andcomments', false);
            g_form.setSectionDisplay('stakeholders', false);
        }
    }
}
