sys_ui_policy.script_false:
function onCondition() {
    if ((g_user.hasRole('x_gis_girm_assessm.software_assessor'))){
        g_form.setSectionDisplay('software_details', true);
        g_form.setSectionDisplay('scope', true);
        g_form.setSectionDisplay('analysis', true);
        g_form.setSectionDisplay('requirements', true);
        g_form.setSectionDisplay('risk_summary', true);
        g_form.setSectionDisplay('appendices', true);
    } else {
        g_form.setSectionDisplay('software_details', false);
        g_form.setSectionDisplay('scope', false);
        g_form.setSectionDisplay('analysis', false);
        g_form.setSectionDisplay('requirements', false);
        g_form.setSectionDisplay('risk_summary', false);
        g_form.setSectionDisplay('appendices', false);
    }
}

sys_ui_policy.script_true:
function onCondition() {
        g_form.setSectionDisplay('software_details', false);
        g_form.setSectionDisplay('scope', false);
        g_form.setSectionDisplay('analysis', false);
        g_form.setSectionDisplay('requirements', false);
        g_form.setSectionDisplay('risk_summary', false);
        g_form.setSectionDisplay('appendices', false);
}
