sys_ui_policy.script_true:
function onCondition() {

    for (var i = 0; i < g_form.elements.length; i++) {
        var el = g_form.elements[i];
        var fieldName = el.fieldName;
        g_form.setMandatory(fieldName, false);
    }

    //hide and unhide sections
    g_form.setSectionDisplay('assessment_questionnaire', false);
    g_form.setSectionDisplay('installer_filedetails', false);
    //g_form.setSectionDisplay('stakeholders', false);

	// read only for Bussiness justification field
	g_form.setMandatory('description', true);
	g_form.setReadOnly('description', false);


}
